import React, { Component } from 'react';
import './App.css';

const clientId = 'fea53a1f5c0834f750d027df42f235043cb7205daa1b6ca365824d670f645d47';

class App extends Component {
  constructor() {
    super();
    this.state = {
      allImgs:[],
      img: '',
    };
  }



  componentDidMount() {
    fetch(`https://api.unsplash.com/photos/?client_id=${clientId}`, {
      method: 'GET',
    })
      .then(res => res.json())
      .then(response => this.setState({img: response[0]}))
      .catch(error => console.error('Error:', error));
  }


  render() {
    return(
      <>
        <div class="content">
          <i className="fa fa-arrow-left left-icon"></i>
          <img className="image" src={this.state.img} alt="backgroundImage"/>
          <i className="fa fa-arrow-right right-icon" onClick={this.handleClick}></i>
        </div>
      </>
    );
  }
}

export default App;
