import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {
  constructor() {
    super();
    this.state = {
			imgs: [],
			loadingState: true
		};
	}

	componentDidMount() {
		this.performSearch();
	}

	performSearch = (query = 'sun') => {
		axios
			.get(
				`https://api.unsplash.com/search/photos/?page=1&per_page=10&query=${query}&client_id=${cred.APP_ID}`
			)
			.then(data => {
				this.setState({ imgs: data.data.results, loadingState: false });
			})
			.catch(err => {
				console.log('Error happened during fetching!', err);
			});
	};

  render() {


    return(
      <>
        <div class="content">
          <i className="fa fa-arrow-left left-icon"></i>
          <img class="image" alt="backgroundImage" data={this.state.imgs}/>
          <i className="fa fa-arrow-right right-icon"></i>
        </div>
      </>
    );
  }
}

export default App;
