import React, { Component } from 'react';
import './App.css';

const clientId = 'fea53a1f5c0834f750d027df42f235043cb7205daa1b6ca365824d670f645d47';

class App extends Component {
  constructor() {
    super();
    this.state = {
      images: [],
      currentIndex:0,
    };
  }



  componentDidMount() {
    fetch(`https://api.unsplash.com/photos/?client_id=${clientId}`, {
      method: 'GET',
    })
      .then(res => res.json())
      .then(response => {
        console.log(response[0]);
        this.setState({images: response})
      })
      .catch(error => console.error('Error:', error));
  }

  goToPrevSlide = () => {
    if(this.state.currentIndex === 0)
      return;

    this.setState({
      currentIndex: this.state.currentIndex - 1
    });
  }

  goToNextSlide = () => {
    if(this.state.currentIndex === this.state.images.length - 1) {
      return;
    }
     this.setState({
      currentIndex: this.state.currentIndex + 1
    });
  }


  render() {
    const { images, currentIndex } = this.state;

    if (!this.state.images.length) return null
    return(
        <div className="content">
          <i className={`fa fa-arrow-left left-icon ${currentIndex===0 ? "deactive" : ""}`} onClick={this.goToPrevSlide}></i>
          <div className="slider">
            <div className="image-wrapper">
              {images.map((item, index) => {
                return (
                  <img
                    className={`image ${index === currentIndex ? "active" : ""}`}
                    src={item.urls.regular}
                    alt="backgroundImage"
                  />
                );
              })
              }
            </div> 
          </div>
          <i
            className={`fa fa-arrow-right right-icon
              ${images.length - 1 === currentIndex ? "deactive" : ""}`}
            onClick={this.goToNextSlide}
          ></i>
          <div className="wrapper">
        <a className="prev" onClick={this.scroll.bind(null,-1)}>&#10094;</a>
        <div className="image-container">
          <div className="image">1</div>
          <div className="image">2</div>
          <div className="image">3</div>
          <div className="image">4</div>
          <div className="image">5</div>
          <div className="image">6</div>
          <div className="image">7</div>
          <div className="image">8</div>
          <div className="image">9</div>
          <div className="image">10</div>
        </div>
        <a className="next" onClick={this.scroll.bind(null,1)}>&#10095;</a>
      </div>
        </div>
    );
  }
}

export default App;
