import React, { Component } from 'react';
import './App.css';

const clientId = 'fea53a1f5c0834f750d027df42f235043cb7205daa1b6ca365824d670f645d47';

class App extends Component {
  constructor() {
    super();
    this.state = {
      images: [],
      currentIndex:0,
    };
  }



  componentDidMount() {
    fetch(`https://api.unsplash.com/photos/?client_id=${clientId}`, {
      method: 'GET',
    })
      .then(res => res.json())
      .then(response => {
        console.log(response[0]);
        this.setState({images: response})
      })
      .catch(error => console.error('Error:', error));
  }

  goToPrevSlide = () => {
    this.setState({
      currentIndex: this.state.currentIndex - 1
    });
  }

  goToNextSlide = () => {

    if(this.state.currentIndex === this.state.images.length - 1) {
      return this.setState({
        currentIndex: 0,

      })
    }
     this.setState({
      currentIndex: this.state.currentIndex + 1
    });
  }


  render() {
    if (!this.state.images.length) return null
    return(
      <>
        <div className="content">
          <i className="fa fa-arrow-left left-icon" onClick={this.goToPrevSlide}></i>
          <div className="image-wrapper">
            <img className="image" src={this.state.images[this.state.currentIndex].urls.regular} alt="backgroundImage"/>
          </div> 
          <i className="fa fa-arrow-right right-icon" onClick={this.goToNextSlide}></i>
        </div>
      </>
    );
  }
}

export default App;
