import React, { Component } from 'react';
import './App.css';

const clientId = 'fea53a1f5c0834f750d027df42f235043cb7205daa1b6ca365824d670f645d47';

class App extends Component {
  constructor() {
    super();
    this.state = {
      img: '',
      currentIndex: 0
    };
  }



  componentDidMount() {
    fetch(`https://api.unsplash.com/photos/?client_id=${clientId}`, {
      method: 'GET',
    })
      .then(res => res.json())
      .then(response => {
        console.log(response[1]);
        this.setState({img: response[1].urls.regular})
      })
      .catch(error => console.error('Error:', error));
  }

  goToPrevSlide = () => {
    this.setState(prevState => ({
      currentIndex: prevState.currentIndex - 1
    }));
  }

  goToNextSlide = () => {
    this.setState({ img: response[1].urls.regular});
  }


  render() {
    return(
      <>
        <div className="content">
          <i className="fa fa-arrow-left left-icon" onClick={this.goToPrevSlide}></i>
          <div className="image-wrapper">
            <img className="image" src={this.state.img} alt="backgroundImage"/>
          </div> 
          <i className="fa fa-arrow-right right-icon" onClick={this.goToNextSlide}></i>
        </div>
      </>
    );
  }
}

export default App;
