import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      allImgs:[],
      randomImg: '',
    };
  }

  handleClick = () => {
    constÂ { allImgs } = this.state;

    let randomNumber = Math.floor(
      Math.random() * allImgs.length
    );

    this.setState({ randomImg: allImgs[randomNumber].url });
  };


  componentDidMount() {
    fetch("https://unsplash.com/oauth/token")
      .then(images => images.json())
      .then(response => {
        const { memes } = response.data;
        this.setState({ 
          allImgs: memes, 
        }, () => {
          this.handleClick();
        });
      });
  }


  render() {
    return(
      <>
        <div class="content">
          <i className="fa fa-arrow-left left-icon"></i>
          <img class="image" src={this.state.randomImg} alt="backgroundImage"/>
          <i className="fa fa-arrow-right right-icon" onClick={this.handleClick}></i>
        </div>
      </>
    );
  }
}

export default App;
